//index.js
// importing the react and react-dom package

import React from 'react'

import ReactDOM from 'react-dom/client'




let diameter = 300;

const headerStyle = (
  <style>
    .profile-pic:
      width: {diameter}px
      height: {diameter}px
  </style>
)

// JSX element, header
const header = (
  <header>
    <div className='header-wrapper'>
      <img source="/asabeneh.jpg" alt="Profile" class="profile-pic"/>
      <p>Asabeneh Yetayeh</p>
      <small>Date: Oct 1, 2020</small>
    </div>
  </header>
)

// JSX element, main
const main = (
  <main>
    <div className='main-wrapper'>
      <p>
        Prerequisite to get started{' '}
        <strong>
          <em>react.js</em>
        </strong>
        :
      </p>
      <ul>
        <li>HTML</li>
        <li>CSS</li>
        <li>JavaScript</li>
      </ul>
    </div>
  </main>
)

// JSX element, footer
const footer = (
  <footer>
    <div className='footer-wrapper'>
      <p>Copyright 2020</p>
    </div>
  </footer>
)

// JSX element, app
const app = (
  <div className='app'>
    {headerStyle}
    {header}
    {main}
    {footer}
  </div>
)








const rootElement = document.getElementById('root')
const root = ReactDOM.createRoot(rootElement)
root.render(app)
